mapper.select:
SELECT date( temp.temp_date ) collect_date, count( * ) collect_count FROM ( SELECT produce_id temp_id, max( collect_date ) temp_date, count( * ) temp_count FROM scada_examines_c
${ew.sqlSegment}
GROUP BY produce_id, date_format(collect_date, '%Y%m%d') ) AS temp GROUP BY date( temp.temp_date )

SELECT table1.num - 1 id, table1.*, TIMESTAMPDIFF( SECOND, table1.collect_date, table2.collect_date ) test_time FROM ( SELECT ( @i := @i + 1 ) AS num, temp1.* FROM ( SELECT product.sn product_sn, plan.sn plan_sn, produce.sn produce_sn, examines.collect_date FROM scada_examines_c examines LEFT JOIN scada_produce produce ON examines.produce_id = produce.id LEFT JOIN scada_plan plan ON produce.plan_id = plan.id LEFT JOIN scada_product product ON plan.product_id = product.id
${ew.sqlSegment}
GROUP BY produce_id ORDER BY collect_date ) temp1, ( SELECT @i := 1 ) b ) table1 LEFT JOIN ( SELECT ( @j := @j + 1 ) AS num, temp2.* FROM ( SELECT product.sn product_sn, plan.sn plan_sn, produce.sn produce_sn, examines.collect_date FROM scada_examines_c examines LEFT JOIN scada_produce produce ON examines.produce_id = produce.id LEFT JOIN scada_plan plan ON produce.plan_id = plan.id LEFT JOIN scada_product product ON plan.product_id = product.id
${ew.sqlSegment}
GROUP BY produce_id ORDER BY collect_date ) temp2, ( SELECT @j := 0 ) d ) table2 ON table1.num = table2.num
